            <td style="text-align: center;">{{ loop.index }}</td>
            <td>{% if item.customer_item_code %}
                    {{ item.customer_item_code }}
                {% else %}
                    {{ item.item_name }}
                {% endif %}
                {% if item.batch_no %}
                    <br><span style="font-size: 9px"><b>Batch:</b>&nbsp;
                    {{ item.batch_no }}</span>
                {% endif %}
            </td>
