                {% if item.gst_hsn_code %}
                    {{ item.gst_hsn_code }}
                {% endif %}
            </td>
            <td style="text-align: center;"> 
                {% if item.qty %}
                    {{ item.qty }}
                {% endif %}
            </td>
            <td style="text-align: center;">
                 {% if item.uom %}
                    {{ item.uom }}
                {% endif %}
            </td>
            <td style="text-align: center;">
                {% if item.total_weight %}
                    {{item.total_weight}}&nbsp;
                    {% if item.weight_uom %}
                        {{item.weight_uom}}
                    {% endif %}<br>
                {% endif %}
                {% if item.qty %}
                    {{item.qty}}&nbsp;
                    {% if item.uom %}
                        {{item.uom}}
                    {% endif %}
                {% endif %}
                {% if item.conversion_factor %}
                    &nbsp;*&nbsp;{{item.conversion_factor}}
                {% endif %}
            </td>
            <td style="text-align: right;">
            {% if item.stock_uom_rate %}
                {{ item.get_formatted("stock_uom_rate") }}
            {% endif %}</td>
            <td style="text-align: right;width:12%;">
            {% if item.amount %}
                {{ item.get_formatted("amount") }}
            {% endif %}</td>
        </tr>
        {%- endfor -%}
        <tr>
            <th></th>
            <th></th>
            <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
            <th style="text-align: center;"><b>{{doc.total_qty}}</b></th>
            <th></th>
            <th style="text-align: center;"></th>
