html:
{% set sovar=frappe.get_doc("Sales Order",doc.locations[1].sales_order) %}
{% set s_address=frappe.get_doc("Address",sovar.shipping_address_name) %}
{% set customer=frappe.get_doc("Customer",doc.customer) %}


<table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
        
        
    </tr>
    

</table>

<div class="row">
    <div class="col-xs-9;" style="text-align : center;">
        <b>{{ _("PICK LIST") }} -</b>&nbsp;
        {{doc.name}}
        
    </div>
    
</div>

<div class="row">
    <div class="col-xs-7">
       
       
     <b>{{ _("Customer") }} -</b>&nbsp;
        {{s_address.address_title}}
 </div>
</div>

<div class="row">
    <div class="col-xs-7">
       
       
     <b>{{ _("Address") }}:</b>&nbsp; 
                {% if s_address.address_line1 %} 
                    {{s_address.address_line1}}, 
                {% endif %}<br>
                {% if s_address.address_line2 %} 
                    {{s_address.address_line2}} ,
                {% endif %}<br>
                {% if s_address.city %} 
                    {{s_address.city}} 
                {% endif %}<br>
                {% if s_address.pincode %} 
                    -{{s_address.pincode}}, 
                {% endif %}<br>
                {% if s_address.gst_state %} 
                    {{s_address.gst_state}}, 
                {% endif %}<br>
 </div>
</div>

        



        
<div class="row">
    <div class="col-xs-7">
        <b>{{ _("SO Number") }}:</b>&nbsp;
        {{sovar.name}}
    </div>
</div>

<div class="row">
    <div class="col-xs-7">
        <b>{{ _("PO Number") }}:</b>&nbsp;
        {{sovar.po_no}}
        
    </div>
</div>



<table style="margin-top: 2px; text-align: center;">
        <tr>
            <th><b>{{ _("Sr No.") }}</b></th>
            <th><b>{{ _("Item Name") }}</b></th>
            <th><b>{{ _("MRP (Rs.)") }}</b></th>
            <th><b>{{ _("Case Size") }}</b></th>
            <th><b>{{ _("Warehouse") }}</b></th>
            <th><b>{{ _("Weight") }}</b></th>

            <th><b>{{ _("Quantity in Units") }}</b></th>
            <th><b>{{ _("Quantity in Cases") }}</b></th>
            <th><b>{{ _("Batch Number") }}</b></th>
            
        </tr>
        {%- for item in doc.locations -%}
        
        <tr>
            <td style="text-align: center;">{{ loop.index }}</td>
            <td>{{ item.item_name }}
                {% if item.customer_item_code %}
                    <br><span style="font-size: 9px"><b>Cust. Item Name</b>&nbsp;
                    {{ item.customer_item_code }}</span>
                {% endif %}
            </td>
            <td style="text-align: left;">
                {%set a=frappe.get_doc("Sales Order Item",item.sales_order_item)%}
                {{ a.get_formatted("price_list_rate") }}
                
            </td>
            <td style="text-align: center;font-size=8px;">
                {%set a=frappe.get_doc("Item",item.item_code)%}
                {% if a.case_size %}
                    {{ a.case_size }}
                {% endif %}
            </td>
            <td style="text-align: center;">
                {{item.warehouse}}        
            </td>
             <td style="text-align: center;">
                {%set a=frappe.get_doc("Item",item.item_code)%}
                {%if a.weight_per_unit%}
                {{"%.2f"| format(a.weight_per_unit *item.qty)}}
                {%endif%}
            </td>
            <td style="text-align: center;">
              
                {% if item.qty %}
                    {{item.qty}}
                {% endif %}&nbsp;
                {% if item.uom %}
                    {{item.uom}}
                {% endif %}&nbsp;
            </td>
            <td style="text-align: center;">
              
               {%set a=frappe.get_doc("Item",item.item_code)%}
                {% if not a.case_size==0 %}
                
                    {{item.qty//(a.case_size|int)}}&nbsp; Case
                    {{item.qty%(a.case_size|int)}}&nbsp; {{item.uom}}
                {% endif %}
                
            </td>
            <td>
                
                {% if item.batch_no is not none %}
                    {{ item.batch_no }}
                    
                {% else %}
                    
                    {"-"}
                    
                {% endif %}
            </td>
             
            
           
        {%- endfor -%}
        <tr>
           
        </tr>
        <tr>
            <th></th>
            <th></th>
            <th></th>
            
            <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
            <th></th>
            <th>{% set vars = {'weight': 0} %}
           
        
                     {%- for item in doc.locations -%}
                    {%set a=frappe.get_doc("Item",item.item_code)%}
                    {% if vars.update({'weight': vars.weight+a.weight_per_unit *item.qty}) %}{% endif %}
                    
                    {%- endfor -%}
                    {{"%.2f"| format( vars.weight) }}&nbsp; KG</th>
            <th style="text-align: center;"> {% set vars = {'jcase': 0} %}
           
        
                     {%- for item in doc.locations -%}
                    {%set a=frappe.get_doc("Item",item.item_code)%}
                    {% if vars.update({'jcase': vars.jcase + item.qty}) %}{% endif %}
                    
                    {%- endfor -%}
                    {{ vars.jcase }}&nbsp; Units</th>
            <th style="text-align: center;"> {% set vars = {'tcase': 0,'ucase': 0} %}
           
        
                     {%- for item in doc.locations -%}
                    {%set a=frappe.get_doc("Item",item.item_code)%}
                    {% if vars.update({'tcase': vars.tcase + item.qty//(a.case_size|int)}) %}{% endif %}
                    {% if vars.update({'ucase': vars.ucase + item.qty%(a.case_size|int)}) %}{% endif %} 
                    {%- endfor -%}
                    {{ vars.tcase }}&nbsp; Cases
                    {{ vars.ucase }}&nbsp; Units
            </th>
            <th></th>
            
        </tr>
        
    </table>
